import React, { useState } from 'react';
import './App.css';

function App() {
  const [text, setText] = useState('');
  const [researchTopic, setResearchTopic] = useState('');
  const [researchGoal, setResearchGoal] = useState('Broad Survey');
  const [timeWindow, setTimeWindow] = useState('Last 5 years');
  const [focusType, setFocusType] = useState('Overview');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [pdfFiles, setPdfFiles] = useState([]);
  const [uploadStatus, setUploadStatus] = useState("");
  const [history, setHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  React.useEffect(() => {
    const savedHistory = JSON.parse(localStorage.getItem("searchHistory")) || [];
    setHistory(savedHistory);
  }, []);

  const handleGeneratePlan = () => {
    setLoading(true);
    setResult(null);
    setTimeout(() => {
      const topic = researchTopic.trim() !== '' ? researchTopic : 'N/A';
      const goal = researchGoal || 'N/A';
      const time = timeWindow || 'N/A';
      const focus = focusType || 'N/A';
      const notes = text.trim() !== '' ? `\nNotes: ${text}` : '';

      setResult({
        plan: `Research Topic: ${topic}\nGoal: ${goal}\nTime Limit: ${time}\nFocus: ${focus}${notes}\n\nThis is a simulated research plan based on your inputs.`
      });

      const newResult = {
        topic,
        goal,
        time,
        focus,
        notes,
        timestamp: new Date().toISOString(),
      };
      const existingHistory = JSON.parse(localStorage.getItem("searchHistory")) || [];
      const updatedHistory = [newResult, ...existingHistory].slice(0, 20);
      localStorage.setItem("searchHistory", JSON.stringify(updatedHistory));
      setHistory(updatedHistory);

      setLoading(false);
    }, 500); // 缩短等待时间
  };

  const handleClear = () => {
    setText('');
    setResearchTopic('');
    setResearchGoal('Broad Survey');
    setTimeWindow('Last 5 years');
    setFocusType('Overview');
    setResult(null);
    setLoading(false);
    setPdfFiles([]);
    setUploadStatus("");
  };

  const handleFileChange = (e) => {
    setPdfFiles(e.target.files);
  };

  const handleUpload = async () => {
    if (pdfFiles.length === 0) {
      setUploadStatus("Please select at least one PDF file.");
      return;
    }

    const formData = new FormData();
    for (let i = 0; i < pdfFiles.length; i++) {
      formData.append("files", pdfFiles[i]);
    }

    setUploadStatus("Uploading...");
    try {
      const response = await fetch("http://127.0.0.1:5000/upload_pdf", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      setUploadStatus(`Uploaded successfully. Extracted keywords: ${data.keywords.join(", ")}`);
    } catch (error) {
      console.error(error);
      setUploadStatus(" Upload failed.");
    }
  };

  return (
    <div className="App">
      <h2>Research Plan Generator</h2>
      <div style={{ marginBottom: '10px' }}>
        <label>
          Research Topic:<br />
          <input
            type="text"
            value={researchTopic}
            onChange={(e) => setResearchTopic(e.target.value)}
            style={{ width: '300px' }}
            placeholder="Enter research topic"
          />
        </label>
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>
          Research Goal:<br />
          <select value={researchGoal} onChange={(e) => setResearchGoal(e.target.value)} style={{ width: '310px' }}>
            <option>Broad Survey</option>
            <option>Deep Dive</option>
            <option>Novelty / Innovation Scan</option>
          </select>
        </label>
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>
          Time Limit:<br />
          <select value={timeWindow} onChange={(e) => setTimeWindow(e.target.value)} style={{ width: '310px' }}>
            <option>Last 5 years</option>
            <option>Last 3 years</option>
            <option>Last 1 year</option>
          </select>
        </label>
      </div>
      <div style={{ marginBottom: '10px' }}>
        Focus Type:<br />
        <label style={{ marginRight: '10px' }}>
          <input
            type="radio"
            value="Overview"
            checked={focusType === 'Overview'}
            onChange={(e) => setFocusType(e.target.value)}
          /> Overview
        </label>
        <label style={{ marginRight: '10px' }}>
          <input
            type="radio"
            value="Detail"
            checked={focusType === 'Detail'}
            onChange={(e) => setFocusType(e.target.value)}
          /> Detail
        </label>
        <label>
          <input
            type="radio"
            value="Summary"
            checked={focusType === 'Summary'}
            onChange={(e) => setFocusType(e.target.value)}
          /> Summary
        </label>
      </div>
      <div style={{ marginBottom: '10px' }}>
        <label>
          Additional Notes:<br />
          <textarea
            placeholder="Enter additional notes here..."
            value={text}
            onChange={(e) => setText(e.target.value)}
            rows="4"
            cols="50"
            style={{ resize: 'none' }}
          />
        </label>
      </div>
      <div style={{ marginBottom: '20px', border: '1px solid #ddd', padding: '10px', borderRadius: '5px' }}>
        <h3>Upload Research PDFs</h3>
        <input type="file" accept="application/pdf" multiple onChange={handleFileChange} />
        <button onClick={handleUpload} disabled={loading} style={{ marginLeft: '10px' }}>
          Upload PDFs
        </button>
        {uploadStatus && <p style={{ marginTop: '10px' }}>{uploadStatus}</p>}
      </div>
      <button onClick={handleGeneratePlan} disabled={loading} style={{ marginRight: '10px' }}>
        {loading ? 'Loading...' : 'Generate Plan'}
      </button>
      <button onClick={handleClear} disabled={loading}>
        Clear
      </button>
      <button onClick={() => setShowHistory(!showHistory)} style={{ marginTop: '10px' }}>
        {showHistory ? 'Hide History' : 'Show History'}
      </button>

      {showHistory && (
        <div style={{ marginTop: '15px', border: '1px solid #ccc', padding: '10px', borderRadius: '4px' }}>
          <h3>Search History (last 7 days)</h3>
          {history.length === 0 ? (
            <p>No history yet.</p>
          ) : (
            <ul>
              {history
                .filter(h => new Date() - new Date(h.timestamp) < 7 * 24 * 60 * 60 * 1000)
                .map((item, index) => (
                  <li key={index} style={{ marginBottom: '5px' }}>
                    <strong>{item.topic}</strong> — {item.goal}, {item.time}, {item.focus}
                    <button
                      onClick={() => {
                        setResearchTopic(item.topic);
                        setResearchGoal(item.goal);
                        setTimeWindow(item.time);
                        setFocusType(item.focus);
                        setText(item.notes);
                      }}
                      style={{ marginLeft: '10px' }}
                    >
                      Load
                    </button>
                  </li>
                ))}
            </ul>
          )}
        </div>
      )}

      {result && (
        <div style={{ marginTop: '20px', whiteSpace: 'pre-wrap', border: '1px solid #ccc', padding: '10px', borderRadius: '4px', backgroundColor: '#f9f9f9' }}>
          <h3>Generated Plan:</h3>
          <p>{result.plan}</p>
        </div>
      )}
    </div>
  );
}

export default App;
